import { RATES_FETCH } from "./types";

const initialState = {
    fetchedRates: {
        USD: { rate: 1 },
        EUR: { rate: null },
        RUB: { rate: null },
        BYN: { rate: null },
        INR: { rate: null },
        CNY: { rate: null },
        TRY: { rate: null },
        GBP: { rate: null },
        CHF: { rate: null },
        JPY: { rate: null },
        THB: { rate: null },
    },
}

export const fetchReducer = (state = initialState, action) => {
    switch (action.type) {
        case RATES_FETCH:
            return {
                ...state, 
                data: action.data,
                EUR: action.data.rates.filter(rate => rate === 'EUR')
            }
        default: return state;
    }
}
