import { CALC_CALCULATE_FROM, CALC_CALCULATE_TO, CALC_AMOUNT, GET_SUMMARY } from "./types";

const initialState = {
    from: 'USD',
    to: 'RUB',
    amount: '',
    summary: ''
}

export const calculatorReducer = (state = initialState, action) => {
    //debugger
    switch(action.type) {
        case CALC_CALCULATE_FROM:
            return {
                ...state, from: action.currency
            }
        case CALC_CALCULATE_TO:
            return {
                ...state, to: action.currency
            }
        case CALC_AMOUNT:
            return {
                ...state, amount: action.amount
            }
        case GET_SUMMARY:
            return {
                ...state, summary: action.summary
            }
        default: return state;
    }
}
