import axios from 'axios';
import { CALC_CALCULATE_FROM, CALC_CALCULATE_TO, EXCHANGE_CALCULATE_FROM, HIDE_LOADER, RATES_FETCH, SHOW_LOADER } from "./types";

const options = {
  method: 'GET',
  url: 'https://exchangerate-api.p.rapidapi.com/rapid/latest/USD',
  headers: {
    'X-RapidAPI-Host': 'exchangerate-api.p.rapidapi.com',
    'X-RapidAPI-Key': '17ae393c6emshdd9cf9a325f83dbp1faa29jsnded4f4454e45'
  }
};

export function ratesFetch() {
    return dispatch => {
        dispatch({ type: SHOW_LOADER })
        axios.request(options).then(function (response) {
            const date = new Date();
            const lastUpdate = date.getDate() + '.' + (date.getMonth() + 1) + '.' + date.getFullYear();
            dispatch({ type: RATES_FETCH, fetchedData: response.data, lastUpdate });
            setTimeout(() => {
              dispatch({type: HIDE_LOADER });
            }, 500)
        }).catch(function (error) {
            console.error(error);
        });
    }
}

export function calculatorFrom(value) {
  return {
    type: CALC_CALCULATE_FROM,
    currency: value
  }
}

export function calculatorTo(value) {
  return {
    type: CALC_CALCULATE_TO,
    currency: value
  }
}

export function exchangeFrom(value) {
  return {
    type: EXCHANGE_CALCULATE_FROM,
    currency: value
  }
}
