import { CALC_CALCULATE_FROM, CALC_CALCULATE_TO } from "./types";

const initialState = {
    from: 'USD',
    to: 'RUB',
    rate: null,
}

export const calculatorReducer = (state = initialState, action) => {
    debugger
    switch(action.type) {
        case CALC_CALCULATE_FROM:
            return {
                ...state, from: action.value
            }
        case CALC_CALCULATE_TO:
            return {
                ...state, to: action.value
            }
        default: return state;
    }
}
