import { RATES_FETCH } from "./types";

const initialState = {
    fetchedData: {},
    currencies: [
        ['USD', 1 ],
        ['EUR', null],
        ['RUB', null],
        ['BYN', null],
        ['INR', null],
        ['CNY', null],
        ['TRY', null],
        ['GBP', null],
        ['CHF', null],
        ['JPY', null],
        ['THB', null],
    ],
    lastUpdate: null
}

export const fetchReducer = (state = initialState, action) => {
    switch (action.type) {
        case RATES_FETCH:
            return {
                ...state, 
                fetchedData: action.fetchedData,    
                currencies: state.currencies.map(currency => Object.entries(action.fetchedData.rates).filter(item => item[0]=== currency[0]).flat()),
                lastUpdate: action.lastUpdate
            }
        default: return state;
    }
}
