import react from 'react';
import { connect } from 'react-redux';

// Components
import Select from '../components/Select';

const Calculator = ({ calculate, currencies }) => {
	console.log(calculate, currencies)
    return (
        <>
            <div class="legend">Select Currencies</div>
			<div class="currency-fields">
				<Select id={ 1 } currencies={ currencies } />
				<div class="svg-arrow-right">
					<svg>
						<use xlinkHref="#right-arrow"></use>
					</svg>
				</div>
				<Select id={ 2 } currencies={ currencies } />
			</div>
			<div class="legend">Amount to Exchange</div>
			<div class="input-field">
				<input type="text"></input>
			</div>
			<div class="legend">Summary</div>
			<div class="summary">
				<span>1234</span>
			</div>
        </>
    )
}

const mapStateToProps = state => {
	//console.log(state)
	return {
		calculate: state.calculator,
		currencies: state.fetch.currencies,
	}
}

export default connect(mapStateToProps, null)(Calculator);
