import { RATES_FETCH } from "./types";

const initialState = {
    fetchedData: {},
    rates: [
        { name: 'USD', rate: 1 },
        { name: 'EUR', rate: null },
        { name: 'RUB', rate: null },
        { name: 'BYN', rate: null },
        { name: 'INR', rate: null },
        { name: 'CNY', rate: null },
        { name: 'TRY', rate: null },
        { name: 'GBP', rate: null },
        { name: 'CHF', rate: null },
        { name: 'JPY', rate: null },
        { name: 'THB', rate: null },
    ],
}

export const fetchReducer = (state = initialState, action) => {
    switch (action.type) {
        case RATES_FETCH:
            return {
                ...state, 
                fetchedData: action.fetchedData,
                rates: state.rates.map(rate => action.fetchedData.rates.filter(item => item.hasOwnProperty(rate.name)))
            }
        default: return state;
    }
}
