import { CALC_CALCULATE_FROM, CALC_CALCULATE_TO, CALC_AMOUNT, GET_SUMMARY, UPDATE_FROM_TO } from "./types";

const initialState = {
    from: 'USD',
    fromRate: '',
    to: 'RUB',
    toRate: '',
    amount: '',
    summary: 0
}

export const calculatorReducer = (state = initialState, action) => {
    //debugger
    switch(action.type) {
        case CALC_CALCULATE_FROM:
            return {
                ...state, from: action.currency, fromRate: action.rate, summary: state.amount * state.toRate / state.fromRate
            }
        case CALC_CALCULATE_TO:
            return {
                ...state, to: action.currency, toRate: action.rate, summary: state.amount * state.fromRate / state.toRate
            }
        case CALC_AMOUNT:
            return {
                ...state, amount: action.amount, summary: state.amount * state.fromRate / state.toRate
            }
        case GET_SUMMARY:
            return {
                ...state, 
                summary: state.amount * state.fromRate / state.toRate
            }
        case UPDATE_FROM_TO:
            return {
                ...state, from: action.from, fromRate: action.fromRate, to: action.to, toRate: action.toRate
            }
        default: return state;
    }
}
