import { RATES_FETCH } from "./types";

const initialState = {
    fetchedData: {},
    fetchedRates: [
        { USD: 1 },
        { EUR: null },
        { RUB: null },
        { BYN: null },
        { INR: null },
        { CNY: null },
        { TRY: null },
        { GBP: null },
        { CHF: null },
        { JPY: null },
        { THB: null },
    ],
}

export const fetchReducer = (state = initialState, action) => {
    switch (action.type) {
        case RATES_FETCH:
            return {
                ...state, 
                fetchedData: action.fetchedData,

            }
        default: return state;
    }
}
