import { CALC_CALCULATE_FROM, CALC_CALCULATE_TO, CALC_AMOUNT, GET_SUMMARY } from "./types";

const initialState = {
    from: 'USD',
    fromRate: '',
    to: 'RUB',
    toRate: '',
    amount: 0,
    summary: 0
}

export const calculatorReducer = (state = initialState, action) => {
    //debugger
    switch(action.type) {
        case CALC_CALCULATE_FROM:
            return {
                ...state, from: action.currency, fromRate: action.rate
            }
        case CALC_CALCULATE_TO:
            return {
                ...state, to: action.currency, toRate: action.rate
            }
        case CALC_AMOUNT:
            return {
                ...state, amount: action.amount
            }
        case GET_SUMMARY:
            return {
                ...state, 
                summary: state.fromRate * state.toRate * state.amount
            }
        default: return state;
    }
}
